import React, { useState } from 'react';
import styled from 'styled-components';

const FilterContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  background-color: #002B5C;
  padding: 1rem;
  gap: 1rem;
`;

const Select = styled.select`
  padding: 0.5rem;
  border: none;
  border-radius: 4px;
`;

const Button = styled.button`
  padding: 0.5rem 1rem;
  background-color: #004b23;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  &:hover {
    background-color: #00331a;
  }
`;

function FilterBar() {
  const [route, setRoute] = useState('');
  const [direction, setDirection] = useState('');
  const [stop, setStop] = useState('');

  const handleSearch = () => {
    // In production, this would trigger a fetch from the prediction API.
    console.log(`Search clicked: Route=${route}, Direction=${direction}, Stop=${stop}`);
  };

  return (
    <FilterContainer>
      <Select value={route} onChange={(e) => setRoute(e.target.value)}>
        <option value="">Select Route</option>
        <option value="OSW10">OSW10 - SUNY Oswego Blue Route</option>
        <option value="OSW1A">OSW1A - Walmart via 104</option>
        <option value="OSW11">OSW11 - SUNY Oswego Green Route</option>
        <option value="OSW1B">OSW1B - Walmart - Hamilton Homes</option>
        <option value="OSW1C">OSW1C - Walmart via Seneca St</option>
        <option value="OSW1D">OSW1D - Walmart via Brandonwood</option>
      </Select>
      <Select value={direction} onChange={(e) => setDirection(e.target.value)}>
        <option value="">Select Direction</option>
        <option value="from-campus">From Campus</option>
        <option value="from-downtown">From Downtown</option>
      </Select>
      <Select value={stop} onChange={(e) => setStop(e.target.value)}>
        <option value="">Select Stop</option>
        <option value="stop1">Stop 1</option>
        <option value="stop2">Stop 2</option>
        <option value="stop3">Stop 3</option>
        {/* Additional stops can be added later */}
      </Select>
      <Button onClick={handleSearch}>Search</Button>
    </FilterContainer>
  );
}

export default FilterBar;
