import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import Navbar from '../components/Navbar';

const RouteDetailContainer = styled.div`
  background-color: #f8f9fa;
  min-height: 100vh;
  padding: 1.5rem;
`;

const ContentWrapper = styled.div`
  max-width: 1000px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
`;

const BackButton = styled.button`
  background: transparent;
  border: none;
  color: #002B5C;
  font-size: 1rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;

  &:hover {
    text-decoration: underline;
  }
`;

const RouteTitle = styled.h1`
  color: #002B5C;
  font-size: 1.8rem;
  margin: 0;
  font-weight: 700;
`;

const StatusBadge = styled.div`
  background-color: ${props => 
    props.status === 'on-time' ? '#d4edda' : 
    props.status === 'delayed' ? '#f8d7da' : 
    props.status === 'approaching' ? '#fff3cd' : '#e2e3e5'
  };
  color: ${props => 
    props.status === 'on-time' ? '#155724' : 
    props.status === 'delayed' ? '#721c24' : 
    props.status === 'approaching' ? '#856404' : '#383d41'
  };
  padding: 0.5rem 1rem;
  border-radius: 20px;
  font-size: 0.9rem;
  font-weight: 500;
`;

const NextArrivalCard = styled.div`
  background: white;
  border-radius: 12px;
  padding: 2rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
  text-align: center;
  margin-bottom: 1.5rem;
`;

const NextArrivalLabel = styled.div`
  color: #666;
  font-size: 1.1rem;
  margin-bottom: 0.5rem;
`;

const NextArrivalTime = styled.div`
  color: #002B5C;
  font-size: 3rem;
  font-weight: 700;
  margin-bottom: 1rem;
`;

const NextArrivalLocation = styled.div`
  color: #666;
  font-size: 1.2rem;
`;

const StopsContainer = styled.div`
  background: white;
  border-radius: 12px;
  padding: 1.5rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
`;

const StopsHeader = styled.h2`
  color: #002B5C;
  font-size: 1.3rem;
  margin: 0 0 1.5rem 0;
  font-weight: 600;
`;

const StopList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const StopItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem;
  border-radius: 8px;
  background-color: ${props => props.active ? '#f0f7ff' : 'transparent'};
  border-left: 4px solid ${props => props.active ? '#002B5C' : 'transparent'};
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #f8f9fa;
  }
`;

const StopName = styled.div`
  color: #333;
  font-weight: ${props => props.active ? '600' : '400'};
`;

const StopTime = styled.div`
  color: #002B5C;
  font-weight: ${props => props.active ? '600' : '500'};
`;

const ActionBar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 1.5rem;
`;

const RefreshButton = styled.button`
  background: #002B5C;
  color: white;
  border: none;
  padding: 0.8rem 1.5rem;
  border-radius: 8px;
  font-weight: 500;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: background-color 0.2s ease;

  &:hover {
    background: #001f42;
  }
`;

const MapButton = styled.button`
  background: white;
  color: #002B5C;
  border: 1px solid #002B5C;
  padding: 0.8rem 1.5rem;
  border-radius: 8px;
  font-weight: 500;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.2s ease;

  &:hover {
    background: #f0f7ff;
  }
`;

const RouteDetail = () => {
  const { routeId } = useParams();
  const navigate = useNavigate();
  const [routeData, setRouteData] = useState(null);
  const [loading, setLoading] = useState(true);

  // Sample route data - in a real app, this would come from an API
  const sampleRoutes = {
    'OSW10': {
      name: 'OSW10 Blue Route',
      status: 'on-time',
      nextArrival: '5 min',
      nextStop: 'SUNY Oswego Campus Center',
      stops: [
        { id: 1, name: 'SUNY Oswego Campus Center', time: '5 min', active: true },
        { id: 2, name: 'Rudolph St & Centennial Dr', time: '8 min', active: false },
        { id: 3, name: 'SUNY Oswego New Campus', time: '12 min', active: false },
        { id: 4, name: 'SUNY Oswego The Village Residence', time: '15 min', active: false },
