import React, { useState } from 'react';
import styled from 'styled-components';
import Navbar from '../components/Navbar';
import { useLocation } from 'react-router-dom';
import ScheduleTable from '../components/ScheduleTable';

const ScheduleContainer = styled.div`
  background-color: #f5f5f5;
  min-height: 100vh;
  padding: 2rem;
`;

const ContentWrapper = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  background: white;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  padding: 2rem;
`;

const ScheduleHeader = styled.h2`
  text-align: center;
  font-family: 'Inter', sans-serif;
  color: #002B5C;
  margin-bottom: 2rem;
  font-size: 1.8rem;
`;

const SelectContainer = styled.div`
  margin-bottom: 2rem;
`;

const StyledSelect = styled.select`
  width: 100%;
  padding: 12px;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  font-size: 1rem;
  color: #333;
  background-color: white;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    border-color: #002B5C;
  }

  &:focus {
    outline: none;
    border-color: #002B5C;
    box-shadow: 0 0 0 3px rgba(0, 43, 92, 0.1);
  }
`;

const Option = styled.option`
  padding: 12px;
`;

const Schedule = () => {
  const location = useLocation();
  const { route: initialRoute, direction, stop } = location.state || { route: '', direction: '', stop: '' };
  const [selectedRoute, setSelectedRoute] = useState(initialRoute);

  const routes = [
    { id: 'OSW10', name: 'OSW10 SUNY Oswego Blue Route' },
    { id: 'OSW20', name: 'OSW20 SUNY Oswego Red Route' },
    { id: 'OSW30', name: 'OSW30 SUNY Oswego Green Route' },
  ];

  return (
    <ScheduleContainer>
      <Navbar />
      <ContentWrapper>
        <ScheduleHeader>Bus Schedule</ScheduleHeader>
        <SelectContainer>
          <StyledSelect 
            value={selectedRoute} 
            onChange={(e) => setSelectedRoute(e.target.value)}
          >
            <Option value="">Select a route</Option>
            {routes.map(route => (
              <Option key={route.id} value={route.id}>
                {route.name}
              </Option>
            ))}
          </StyledSelect>
        </SelectContainer>
        {selectedRoute && <ScheduleTable route={selectedRoute} />}
      </ContentWrapper>
    </ScheduleContainer>
  );
};

export default Schedule;
