import React from 'react';
import styled from 'styled-components';

const Card = styled.div`
  background-color: #004b23;
  padding: 1rem;
  border-radius: 8px;
  margin-bottom: 1rem;
  text-align: center;
`;

const CardTitle = styled.h3`
  margin: 0;
  font-size: 1.2rem;
`;

const BusTime = styled.p`
  font-size: 1.5rem;
  margin: 0.5rem 0 0;
`;

function NextBusCard() {
  // For demonstration, using a static value. Future integration will calculate this dynamically.
  const nextBusTime = "03:09 PM";

  return (
    <Card>
      <CardTitle>Next Bus Arrival</CardTitle>
      <BusTime>{nextBusTime}</BusTime>
    </Card>
  );
}

export default NextBusCard;
