import React from 'react';
import styled from 'styled-components';
import FilterBar from '../components/FilterBar';

const HomeWrapper = styled.div`
  min-height: 80vh;
  background: linear-gradient(135deg, #0e87cc 0%, #002B5C 100%);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const ContentBox = styled.div`
  background-color: rgba(255, 255, 255, 0.9);
  padding: 2rem;
  border-radius: 12px;
  text-align: center;
  max-width: 600px;
  width: 90%;
`;

const Title = styled.h1`
  font-size: 2rem;
  margin-bottom: 1rem;
  color: #002b5c;
`;

const Subtitle = styled.p`
  font-size: 1rem;
  color: #444;
  margin-bottom: 1.5rem;
`;

function Home({
  selectedRoute,
  setSelectedRoute,
  selectedDirection,
  setSelectedDirection,
  selectedStop,
  setSelectedStop
}) {
  // Example onSearch handler:
  const handleSearch = () => {
    // For now, just console.log
    console.log('Search clicked', {
      route: selectedRoute,
      direction: selectedDirection,
      stop: selectedStop
    });
    alert(`Searching for route=${selectedRoute}, direction=${selectedDirection}, stop=${selectedStop}`);
  };

  return (
    <HomeWrapper>
      <ContentBox>
        <Title>Plan Your Trip</Title>
        <Subtitle>Select your route, direction, and stop to begin.</Subtitle>
        <FilterBar
          selectedRoute={selectedRoute}
          setSelectedRoute={setSelectedRoute}
          selectedDirection={selectedDirection}
          setSelectedDirection={setSelectedDirection}
          selectedStop={selectedStop}
          setSelectedStop={setSelectedStop}
          onSearch={handleSearch}
        />
      </ContentBox>
    </HomeWrapper>
  );
}

export default Home;
