import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import FilterBar from '../components/FilterBar';
import NextBusCard from '../components/NextBusCard';

const HomeContainer = styled.div`
  background-color: #f8f9fa;
  min-height: 100vh;
  padding: 2rem;
`;

const ContentWrapper = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 300px;
  gap: 2rem;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const MainContent = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const Title = styled.h1`
  color: #002B5C;
  text-align: center;
  font-family: 'Inter', sans-serif;
  font-size: 2.5rem;
  margin-bottom: 1rem;
  font-weight: 700;
`;

const FeatureGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin-top: 2rem;
`;

const FeatureCard = styled.div`
  background: white;
  border-radius: 16px;
  padding: 2rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
  transition: transform 0.2s ease, box-shadow 0.2s ease;
  cursor: pointer;

  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 8px 12px rgba(0, 0, 0, 0.1);
  }
`;

const FeatureIcon = styled.div`
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: #002B5C;
`;

const FeatureTitle = styled.h2`
  color: #002B5C;
  font-size: 1.5rem;
  margin-bottom: 1rem;
  font-weight: 600;
`;

const FeatureDescription = styled.p`
  color: #666;
  line-height: 1.6;
  margin-bottom: 1.5rem;
`;

const ActionButton = styled.button`
  background: #002B5C;
  color: white;
  border: none;
  padding: 0.8rem 1.5rem;
  border-radius: 8px;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.2s ease;

  &:hover {
    background: #001f42;
  }
`;

const WeatherCard = styled.div`
  background: white;
  border-radius: 16px;
  padding: 1.5rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
  height: fit-content;
`;

const WeatherHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 1.5rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid #eee;
`;

const WeatherTitle = styled.h2`
  color: #002B5C;
  font-size: 1.2rem;
  margin: 0;
  font-weight: 600;
`;

const WeatherInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const WeatherDetail = styled.div`
  display: flex;
  justify-content: space-between;
  color: #666;
  font-size: 0.95rem;
  padding: 0.5rem 0;
`;

const WeatherValue = styled.span`
  color: #002B5C;
  font-weight: 500;
`;

const Temperature = styled.div`
  font-size: 3rem;
  color: #002B5C;
  font-weight: 600;
  text-align: center;
  margin: 1.5rem 0;
`;

const WeatherIcon = styled.div`
  font-size: 2.5rem;
  text-align: center;
  margin: 1rem 0;
`;

const LoadingSpinner = styled.div`
  text-align: center;
  color: #666;
  padding: 2rem;
`;

const ErrorMessage = styled.div`
  text-align: center;
  color: #dc3545;
  padding: 2rem;
  background: #fff5f5;
  border-radius: 8px;
  margin: 1rem 0;
`;

const Home = () => {
  const navigate = useNavigate();
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        setLoading(true);
        setError(null);
        console.log('Fetching weather data...');

        const response = await fetch(
          `https://api.openweathermap.org/data/2.5/weather?q=Oswego,NY,US&units=imperial&appid=ab385b44b9b33d12cf4397188ca4381b`,
          {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
            },
          }
        );

        console.log('Response status:', response.status);

        if (!response.ok) {
          const errorData = await response.json();
          console.error('Weather API error:', errorData);
          throw new Error(`Weather data not available: ${errorData.message || response.statusText}`);
        }

        const data = await response.json();
        console.log('Weather data received:', data);

        if (!data || !data.main) {
          throw new Error('Invalid weather data format');
        }

        setWeather(data);
      } catch (error) {
        console.error('Error fetching weather:', error);
        setError(error.message || 'Unable to load weather data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchWeather();
  }, []);

  const renderWeatherContent = () => {
    if (loading) {
      return <LoadingSpinner>Loading weather data...</LoadingSpinner>;
    }

    if (error) {
      return <ErrorMessage>{error}</ErrorMessage>;
    }

    if (!weather || !weather.main) {
      return <ErrorMessage>Weather data not available</ErrorMessage>;
    }

    return (
      <>
        <WeatherIcon>
          {weather.weather?.[0]?.main === 'Clear' ? '‚òÄÔ∏è' : 
           weather.weather?.[0]?.main === 'Clouds' ? '‚òÅÔ∏è' : 
           weather.weather?.[0]?.main === 'Rain' ? 'üåßÔ∏è' : 
           weather.weather?.[0]?.main === 'Snow' ? 'üå®Ô∏è' : 'üå§Ô∏è'}
        </WeatherIcon>
        <Temperature>
          {Math.round(weather.main.temp)}¬∞F
        </Temperature>
        <WeatherInfo>
          <WeatherDetail>
            <span>Feels Like</span>
            <WeatherValue>{Math.round(weather.main.feels_like)}¬∞F</WeatherValue>
          </WeatherDetail>
          <WeatherDetail>
            <span>Humidity</span>
            <WeatherValue>{weather.main.humidity}%</WeatherValue>
          </WeatherDetail>
          <WeatherDetail>
            <span>Wind</span>
            <WeatherValue>{Math.round(weather.wind.speed)} mph</WeatherValue>
          </WeatherDetail>
        </WeatherInfo>
      </>
    );
  };

  return (
    <HomeContainer>
      <Navbar />
      <ContentWrapper>
        <MainContent>
          <Title>CENTRO BUS PREDICTOR</Title>
          <FeatureGrid>
            <FeatureCard onClick={() => navigate('/schedule')}>
              <FeatureIcon>üöå</FeatureIcon>
              <FeatureTitle>Bus Schedule</FeatureTitle>
              <FeatureDescription>
                View detailed bus schedules for all routes. Plan your journey with accurate departure times and route information.
              </FeatureDescription>
              <ActionButton>View Schedule</ActionButton>
            </FeatureCard>

            <FeatureCard>
              <FeatureIcon>‚è±Ô∏è</FeatureIcon>
              <FeatureTitle>Real-Time Predictions</FeatureTitle>
              <FeatureDescription>
                Get accurate, real-time predictions for bus arrivals. Know exactly when your bus will arrive at your stop.
              </FeatureDescription>
              <ActionButton>Check Predictions</ActionButton>
            </FeatureCard>
          </FeatureGrid>
        </MainContent>

        <WeatherCard>
          <WeatherHeader>
            <WeatherTitle>Oswego, NY Weather</WeatherTitle>
          </WeatherHeader>
          {renderWeatherContent()}
        </WeatherCard>
      </ContentWrapper>
    </HomeContainer>
  );
};

export default Home;
