import React, { useState } from 'react';
import styled from 'styled-components';

const TrackerContainer = styled.div`
  padding: 2rem;
  background-color: #002B5C;
  color: white;
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 1rem;
`;

const ShowMapButton = styled.button`
  padding: 0.5rem 1rem;
  background-color: #004b23;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  &:hover {
    background-color: #00331a;
  }
`;

const MapPlaceholder = styled.div`
  margin-top: 1rem;
  height: 300px;
  background-color: #333;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 8px;
`;

function Tracker() {
  const [showMap, setShowMap] = useState(false);

  return (
    <TrackerContainer>
      <Title>Tracker</Title>
      <p>Real-time bus tracking info will appear here.</p>
      <ShowMapButton onClick={() => setShowMap((prev) => !prev)}>
        {showMap ? 'Hide Map' : 'Show Map'}
      </ShowMapButton>
      {showMap && <MapPlaceholder>Map goes here</MapPlaceholder>}
    </TrackerContainer>
  );
}

export default Tracker;
