import React from 'react';
import styled from 'styled-components';

const FilterContainer = styled.div`
  background-color: #fff;
  border: 1px solid #ccc;
  padding: 1rem;
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  gap: 1rem;
  border-radius: 8px;
`;

const Label = styled.label`
  font-weight: 500;
  margin-right: 0.5rem;
`;

const Select = styled.select`
  padding: 0.4rem;
  border-radius: 4px;
  border: 1px solid #ccc;
  font-size: 0.9rem;
`;

const Button = styled.button`
  background-color: #004b23;
  color: #fff;
  border: none;
  border-radius: 4px;
  padding: 0.6rem 1.2rem;
  cursor: pointer;
  font-weight: bold;
  &:hover {
    background-color: #006b35;
  }
`;

// Placeholder route, direction, and stop data
const routeOptions = [
  { code: '', name: 'Select Route' },
  { code: 'OSW10', name: 'SUNY Oswego Blue Route' },
  { code: 'OSW11', name: 'SUNY Oswego Green Route' },
  { code: 'OSW1A', name: 'Walmart via 104' },
  { code: 'OSW1B', name: 'Walmart - Hamilton Homes' },
  { code: 'OSW1C', name: 'Walmart via Seneca St' },
  { code: 'OSW1D', name: 'Walmart via Brandonwood' },
];

const directionOptions = [
  { code: '', name: 'From Direction' },
  { code: 'fromCampus', name: 'From Campus' },
  { code: 'fromDowntown', name: 'From Downtown' },
];

const stopOptions = [
  { code: '', name: 'Select Stop' },
  { code: 'campusCenter', name: 'Campus Center' },
  { code: 'rudolphSt', name: 'Rudolph St' },
  { code: 'walmart', name: 'Walmart' },
];

function FilterBar({
  selectedRoute,
  setSelectedRoute,
  selectedDirection,
  setSelectedDirection,
  selectedStop,
  setSelectedStop,
  onSearch,
}) {
  return (
    <FilterContainer>
      {/* Route Dropdown */}
      <div>
        <Label htmlFor="route">Route:</Label>
        <Select
          id="route"
          value={selectedRoute}
          onChange={(e) => setSelectedRoute(e.target.value)}
        >
          {routeOptions.map((r) => (
            <option key={r.code} value={r.code}>
              {r.name}
            </option>
          ))}
        </Select>
      </div>

      {/* Direction Dropdown */}
      <div>
        <Label htmlFor="direction">Direction:</Label>
        <Select
          id="direction"
          value={selectedDirection}
          onChange={(e) => setSelectedDirection(e.target.value)}
        >
          {directionOptions.map((d) => (
            <option key={d.code} value={d.code}>
              {d.name}
            </option>
          ))}
        </Select>
      </div>

      {/* Stop Dropdown */}
      <div>
        <Label htmlFor="stop">Stop:</Label>
        <Select
          id="stop"
          value={selectedStop}
          onChange={(e) => setSelectedStop(e.target.value)}
        >
          {stopOptions.map((s) => (
            <option key={s.code} value={s.code}>
              {s.name}
            </option>
          ))}
        </Select>
      </div>

      {/* Search Button */}
      <Button onClick={onSearch}>Search</Button>
    </FilterContainer>
  );
}

export default FilterBar;
