import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import MapView from "../components/MapView";
import ScheduleTable from "../components/ScheduleTable";

function Tracker() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const selectedRoute = queryParams.get("route") || "All Routes";

  const [showSchedule, setShowSchedule] = useState(false);

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Live Tracker</h2>
      <p>
        Displaying data for: <strong>{selectedRoute}</strong>
      </p>
      <MapView selectedRoute={selectedRoute} />

      {selectedRoute !== "All Routes" && (
        <div style={{ marginTop: "1rem" }}>
          <button
            onClick={() => setShowSchedule(!showSchedule)}
            style={{
              padding: "0.5rem 1rem",
              backgroundColor: "#FFC107",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
              marginBottom: "1rem",
            }}
          >
            {showSchedule ? "Hide Official Schedule" : "Show Official Schedule"}
          </button>
          {showSchedule && <ScheduleTable route={selectedRoute} />}
        </div>
      )}
    </div>
  );
}

export default Tracker;
