import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import styled from "styled-components";
import MapView from "../components/MapView";
import ScheduleTable from "../components/ScheduleTable";
import FilterBar from "../components/FilterBar";

const TrackerContainer = styled.div`
  padding: 2rem;
`;

function Tracker({ selectedRoute, onChange }) {
  const [showMap, setShowMap] = useState(false);

  return (
    <TrackerContainer>
      <h2>Live Tracker</h2>
      <FilterBar selectedRoute={selectedRoute} onChange={onChange} />
      <p>Displaying data for: <strong>{selectedRoute}</strong></p>
      <button onClick={() => setShowMap(!showMap)}>
        {showMap ? "Hide Map" : "Show Map"}
      </button>
      {showMap && <MapView selectedRoute={selectedRoute} />}
      {selectedRoute !== "All Routes" && <ScheduleTable route={selectedRoute} />}
    </TrackerContainer>
  );
}

export default Tracker;
