import React from 'react';
import styled from 'styled-components';
import Navbar from '../components/Navbar';
import FilterBar from '../components/FilterBar';
import NextBusCard from '../components/NextBusCard';

const HomeContainer = styled.div`
  padding: 2rem;
  max-width: 800px;
  margin: 0 auto;
  text-align: center;
`;

const Headline = styled.h1`
  font-size: 2.5rem;
  color: #002B5C;
  margin-bottom: 1rem;
`;

const Subheading = styled.p`
  font-size: 1.2rem;
  color: #004b23;
  margin-bottom: 2rem;
`;

const Home = () => {
  return (
    <>
      <Navbar />
      <HomeContainer>
        <Headline>CENTRO BUS PREDICTOR</Headline>
        <Subheading>Your campus transit planner</Subheading>
        <FilterBar />
        {/* Use NextBusCard to display placeholder prediction info */}
        <NextBusCard predictedTime={"3:09 PM"} />
      </HomeContainer>
    </>
  );
};

export default Home;
