import React, { useState } from 'react';
import styled from 'styled-components';
import FilterBar from '../components/FilterBar';
import MapView from '../components/MapView';
import ScheduleTable from '../components/ScheduleTable';

const TrackerContainer = styled.div`
  padding: 2rem;
`;

const ToggleButton = styled.button`
  padding: 0.5rem 1rem;
  background-color: #FFC107;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  margin-bottom: 1rem;
  font-size: 1rem;
`;

function Tracker({ selectedRoute, onChange }) {
  const [showMap, setShowMap] = useState(false);

  return (
    <TrackerContainer>
      <h2>Live Tracker</h2>
      <FilterBar selectedRoute={selectedRoute} onChange={onChange} />
      <p>
        Displaying data for: <strong>{selectedRoute || 'All Routes'}</strong>
      </p>
      <ToggleButton onClick={() => setShowMap(!showMap)}>
        {showMap ? 'Hide Map' : 'Show Map'}
      </ToggleButton>
      {showMap && <MapView selectedRoute={selectedRoute} />}
      {selectedRoute && <ScheduleTable route={selectedRoute} />}
    </TrackerContainer>
  );
}

export default Tracker;
