import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import FilterBar from '../components/FilterBar';
import HeroSection from '../components/HeroSection';

function Home() {
  const [selectedRoute, setSelectedRoute] = useState('');

  return (
    <div>
      <HeroSection />
      <div style={{ padding: '2rem', textAlign: 'center' }}>
        <p style={{ fontSize: '1.1rem', margin: '1rem 0' }}>
          Track your campus buses in real-time. Built for SUNY Oswego students.
        </p>
        <FilterBar selectedRoute={selectedRoute} onChange={setSelectedRoute} />
        {selectedRoute && (
          <p style={{ fontSize: '1rem', color: '#333' }}>
            You have selected: <strong>{selectedRoute}</strong>
          </p>
        )}
        <Link to={`/tracker?route=${encodeURIComponent(selectedRoute)}`}>
          <button style={{
            padding: '0.75rem 1.5rem',
            backgroundColor: '#004b23',
            color: '#fff',
            border: 'none',
            borderRadius: '8px',
            fontSize: '1rem',
            cursor: 'pointer'
          }}>
            Go to Live Tracker
          </button>
        </Link>
      </div>
    </div>
  );
}

export default Home;
