import React from 'react';



function HeroSection() {
  const heroStyle = {
    backgroundImage: 'Hero.png',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundAttachment: 'fixed', // Creates a parallax-like effect
    height: '80vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-start',
    color: '#fff',
    position: 'relative'
  };

  const overlayStyle = {
    backgroundColor: 'rgba(0, 0, 0, 0.4)',
    width: '100%',
    height: '100%',
    position: 'absolute',
    top: 0,
    left: 0
  };

  const contentStyle = {
    position: 'relative',
    marginLeft: '5%',
    zIndex: 2,
    maxWidth: '600px'
  };

  const buttonStyle = {
    padding: '1rem 2rem',
    backgroundColor: '#FFC107',
    border: 'none',
    borderRadius: '8px',
    fontSize: '1.2rem',
    cursor: 'pointer',
    marginTop: '1rem'
  };

  return (
    <div style={heroStyle}>
      <div style={overlayStyle}></div>
      <div style={contentStyle}>
        <h1 style={{ fontSize: '3rem', fontWeight: 'bold' }}>SUNY Oswego Bus Predictor</h1>
        <p style={{ fontSize: '1.5rem', marginTop: '1rem' }}>Real-time Bus Tracking for Students</p>
        <a href="/tracker" style={{ textDecoration: 'none' }}>
          <button style={buttonStyle}>Track Your Bus</button>
        </a>
      </div>
    </div>
  );
}

export default HeroSection;
