import React from 'react';
import styled from 'styled-components';

const FilterWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Label = styled.label`
  margin-right: 0.5rem;
  font-weight: bold;
`;

const Select = styled.select`
  padding: 0.5rem;
  border-radius: 4px;
  border: 1px solid #ccc;
`;

const routes = [
  { code: "OSW10", name: "SUNY Oswego Blue Route" },
  { code: "OSW11", name: "SUNY Oswego Green Route" },
  { code: "OSW1A", name: "Walmart via 104" },
  { code: "OSW1B", name: "Walmart - Hamilton Homes" },
  { code: "OSW1C", name: "Walmart via Seneca St" },
  { code: "OSW1D", name: "Walmart via Brandonwood" }
];

function FilterBar({ selectedRoute, onChange }) {
  return (
    <FilterWrapper>
      <Label htmlFor="route-select">Select Route:</Label>
      <Select id="route-select" value={selectedRoute} onChange={(e) => onChange(e.target.value)}>
        <option value="">All Routes</option>
        {routes.map((route, index) => (
          <option key={index} value={route.code}>{route.name}</option>
        ))}
      </Select>
    </FilterWrapper>
  );
}

export default FilterBar;
