import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import styled from "styled-components";
import MapView from "../components/MapView";
import ScheduleTable from "../components/ScheduleTable";

const TrackerContainer = styled.div`
  padding: 2rem;
`;

const TrackerHeader = styled.h2`
  margin-bottom: 1rem;
`;

const ToggleButton = styled.button`
  padding: 0.5rem 1rem;
  background-color: #FFC107;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  margin-bottom: 1rem;
  font-size: 1rem;
`;

const TrackerInfo = styled.p`
  margin-bottom: 1rem;
`;

function Tracker({ selectedRoute }) {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const route = queryParams.get("route") || "All Routes";

  const [showMap, setShowMap] = useState(false);

  return (
    <TrackerContainer>
      <TrackerHeader>Live Tracker</TrackerHeader>
      <TrackerInfo>
        Displaying data for: <strong>{route}</strong>
      </TrackerInfo>
      <ToggleButton onClick={() => setShowMap(!showMap)}>
        {showMap ? "Hide Map" : "Show Map"}
      </ToggleButton>
      {showMap && <MapView selectedRoute={route} />}
      {route !== "All Routes" && <ScheduleTable route={route} />}
    </TrackerContainer>
  );
}

export default Tracker;
